import React,{useState} from "react";

const App = () => {
  const [showImage, setShowImage] = useState(false);
  const toggle = () => {
    setShowImage(!showImage);
  }

  return (
    <div className="App">
      <button onClick={toggle}>
        {this.state.showImage ? 'Esconder' : 'Mostrar'}
      </button>
      {this.state.showImage && (
        <h1>Opa</h1>
      )}
    </div>
  )
}

export default App;
