import React,{Component} from "react";

class App extends Component{
  constructor(props){
    super(props);

    this.state = {
      showImage: false,
    };
  }
  toggle = () => {
    this.setState({
      showImage: !this.state.showImage,
    });
  }

  render(){
    return (
      <div className="App">
        <button onClick={this.toggle}>
          {this.state.showImage ? 'Esconder' : 'Mostrar'}
        </button>
        {this.state.showImage && (
          <h1>Opa</h1>
        )}
      </div>
    )
  }
}

export default App;
